import React from 'react';

const PositionedText = ({ text, width, height, top, left }) => {
  return (
    <div
      className={`absolute ${top} ${left} ${width} ${height} transform -translate-x-1/2 -translate-y-1/2`}>
      <h1 className="text-3xl font-bold">{text}</h1>
    </div>
  );
};

const StartYourJourneyCTA = () => {
  const shapeStyle = {
    position: 'absolute', // Positioning the shape using absolute positioning
    left: '319px',        // X position
    top: '235px',          // Y position
    width: '593px',       // Width
    height: '93px',      // Height
    backgroundColor: '#8D6145', // Background color
    borderRadius: '16px', // Corner radius
    opacity: 1,           // 100% opacity
    // boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.5)', // Drop shadow
    // overflowY: 'auto',    // Enable vertical scrolling
    zIndex: 40
  };

  return (
    <div style={{ position: 'relative' }}>
      <div style={shapeStyle}>
          <h1 className="text-white font-teachers_medium justify-center" 
            style={{
              textAlign: 'center',
              lineHeight: '100px', 
              letterSpacing: '0.5px', 
              fontSize: '35px',
              }}>START YOUR JOURNEY NOW
          </h1>

      </div>

    <PositionedText 
        text="Your loved ones feel special"
        w="w-5/6"       // Adjust width as needed
        h="h-auto"      // Auto height to fit content
        top="top-1/4"   // Set top positioning
        left="left-1/2" // Center horizontally
      />
    </div>



  );
};

export default StartYourJourneyCTA;
