import React from 'react';

const PositionedText = ({ text, width, height, top, left }) => {
  return (
    <div
      className={`absolute ${top} ${left} ${width} ${height} transform -translate-x-1/2 -translate-y-1/2 bg-white p-4 rounded-lg shadow-md`}>
      <h1 className="text-3xl font-bold">{text}</h1>
    </div>
  );
};

const StartYourJourney = () => {
  const shapeStyle = {
    position: 'absolute', // Positioning the shape using absolute positioning
    left: '270px',        // X position
    top: '27px',          // Y position
    width: '674px',       // Width
    height: '405px',      // Height
    backgroundColor: '#FFFFFF', // Background color
    borderRadius: '16px', // Corner radius
    opacity: 1,           // 100% opacity
    boxShadow: '4px 0px 4px rgba(0, 0, 0, 0.5), 0px 4px 4px rgba(0, 0, 0, 0.5)', // Drop shadow
    // overflowY: 'auto',    // Enable vertical scrolling
    zIndex: 30
  };

  return (
    <div style={{position: 'relative'}}>
      <div style={shapeStyle}></div>
      <PositionedText 
        text="Donâ€™t forget to make your loved ones feel special "
        width="645px"
        height="49px"
        top="109px"
        left="295px"
      />
    </div>
  );
};

export default StartYourJourney;
